const form=document.querySelector("#form"),firstName=document.querySelector("#firstName"),lastName=document.querySelector("#lastName"),email=document.querySelector("#email"),refername=document.querySelector("#refername"),authorizeWork=document.querySelector("#authorizework"),authorizeInterview=document.querySelector("#authorizeinterview"),mobile=document.querySelector("#mobile"),clearButton=document.getElementById("clearButton");function validateInputs(){const firstNameValue=firstName.value.trim(),lastNameValue=lastName.value.trim(),emailValue=email.value.trim(),refernameValue=refername.value.trim(),mobileValue=mobile.value.trim(),authorizeWorkValue=authorizeWork.value,authorizeInterviewValue=authorizeInterview.value;return""===firstNameValue?setError(firstName,"First name is required"):setSuccess(firstName),""===lastNameValue?setError(lastName,"Last name is required"):setSuccess(lastName),""===emailValue?setError(email,"Email is required"):isValidEmail(emailValue)?setSuccess(email):setError(email,"Enter a valid email address"),""===refernameValue?setError(refername,"reference name is required"):setSuccess(refername),"-None-"===authorizeWorkValue?setError(authorizeWork,"Please select an option"):setSuccess(authorizeWork),"-None-"===authorizeInterviewValue?setError(authorizeInterview,"Please select an option"):setSuccess(authorizeInterview),""===mobileValue?setError(mobile,"Enter a valid mobile number"):setSuccess(mobile),!(""===firstNameValue||""===lastNameValue||""===emailValue||!isValidEmail(emailValue))}function setError(element,message){const errorElement=element.nextElementSibling;errorElement.innerText=message}function setSuccess(element){const errorElement=element.nextElementSibling;errorElement.innerText=""}function isValidEmail(email){const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return emailRegex.test(email)}function saveFormValues(){const formValues={firstName:firstName.value.trim(),lastName:lastName.value.trim(),email:email.value.trim(),refername:refername.value.trim(),authorizeWork:authorizeWork.value.trim(),authorizeInterview:authorizeInterview.value.trim(),mobile:mobile.value.trim()};try{localStorage.setItem("formValues",JSON.stringify(formValues))}catch(error){console.error("Error saving to local storage:",error.message)}}function loadFormValues(){try{const storedFormValues=localStorage.getItem("formValues");if(storedFormValues){const parsedFormValues=JSON.parse(storedFormValues);firstName.value=parsedFormValues.firstName,lastName.value=parsedFormValues.lastName,email.value=parsedFormValues.email,refername.value=parsedFormValues.refername,authorizeWork.value=parsedFormValues.authorizeWork,authorizeInterview.value=parsedFormValues.authorizeInterview,mobile.value=parsedFormValues.mobile}}catch(error){console.error("Error loading from local storage:",error.message)}}function clearForm(){const inputs=form.querySelectorAll("input, select");inputs.forEach(input=>{input.value=""});const errorElements=form.querySelectorAll(".error");errorElements.forEach(errorElement=>{errorElement.innerText=""});try{localStorage.removeItem("formValues")}catch(error){console.error("Error clearing local storage:",error.message)}}window.onload=function(){loadFormValues()},form.addEventListener("submit",e=>{validateInputs()?saveFormValues():e.preventDefault()}),clearButton.addEventListener("click",()=>{clearForm()});